import random as rdprint("PAPIER KAMIEŃ NOŻYCE\n")ans = input("Napisz swój wybór: ")def solve(ans):    counter = rd.choice(["rock","paper","scissors"])# =============================================================================#     counter = ""#     rand = rd.randrange(1,4)#     if rand == 1:#         counter = "paper"#     elif rand == 2:#         counter = "rock"#     else:#         counter = "scissors"# =============================================================================        print("Computer: " + counter)    print("")    if ans == "paper" and counter == "rock":        print("You win!")    elif ans == "paper" and counter == "paper":        print("Draw!")    elif ans == "paper" and counter == "scissors":        print("You lose!")    elif ans == "rock" and counter == "paper":        print("You lose!")    elif ans == "rock" and counter == "rock":        print("Draw!")    elif ans == "rock" and counter == "scissors":        print("You win!")    elif ans == "scissors" and counter == "rock":        print("You lose!")    elif ans == "scissors" and counter == "scissors":        print("Draw!")    elif ans == "scissors" and counter == "paper":        print("You win!")    else:        print("You didn't type your answer!")    solve(ans)